import { useState, useEffect} from "react"
import axios from "axios"
import Grid from "./components/grid"
import Navbar from "./components/Navbar"
import SearchBar from "./components/searchBar"

const App = () => {

const [discover, setDiscover] = useState([])
const [tendencia, setTendencia] = useState([])
const [search, setSearch] = useState([])

useEffect(()=>{
axios.get('http://localhost:3000/api/discover')
.then(data => data.data)
.then(data => setDiscover(data.results))
}, [])

useEffect(()=>{
axios.get('http://localhost:3000/api/tendencia')
.then(data => data.data)
.then(data => setTendencia(data.results))
}, [])

const handleSearch = (e) => {
 e.preventDefault()

axios.get(`http://localhost:3000/api/search/${input}`)
  .then(data => data.data)
  .then(data => App.setSearch(data.results))
.then(()=> console.log(App.search)) 
}

return (
  <div>
<Navbar/>
<SearchBar handleSearch={handleSearch}/>

{search == [] ?

 <div><p class="title is-3">Tu Busqueda: </p> 
 <Grid movies={search}/></div>  : 

<div>
<p class="title is-3">Descubre: </p> 
 <Grid movies={discover}/>
<p class="title is-3">Tendencias: </p>
 <Grid movies={tendencia}/>
</div>} 


  </div>
)
}

export default App;
