import { useState, useEffect} from "react"
import axios from "axios"
import Grid from "./components/grid"
import Navbar from "./components/Navbar"
import SearchBar from "./components/searchBar"
import Modal from "./components/modal"

const App = () => {

const [discover, setDiscover] = useState([])
const [tendencia, setTendencia] = useState([])
const [time, setTime] = useState('day')
const [search, setSearch] = useState([])
const [idSearch, setIdSearch] = useState([])


useEffect(()=>{
axios.get('http://localhost:3000/api/discover')
.then(data => data.data)
.then(data => setDiscover(data.results.slice(0,12)))
}, [])

useEffect(()=>{
axios.get(`http://localhost:3000/api/tendencia/${time}`)
.then(data => data.data)
.then(data => setTendencia(data.results.slice(0,12)))
}, [time])

const handleSearch = (input) => {
axios.get(`http://localhost:3000/api/search/${input}`)
  .then(data => data.data)
  .then(data => setSearch(data.results))
}

const handleIdSearch = (id,media) => {
axios.get(`http://localhost:3000/api/search/${media}/${id}`)
  .then(data => setIdSearch(data.data))
}

 const handleChange = () => {
time === 'day' ? setTime("week") : setTime("day")
}

return (
  <div>
<Navbar/>
<SearchBar handleSearch={handleSearch}/>

{search.length != 0 ?
<div>
 {idSearch.length !== 0? <Modal movies={idSearch}/> : ""}
 <p class="title is-3">Tu Busqueda: </p> 
 <Grid movies={search} handleIdSearch={handleIdSearch}/></div>  : 

<div>
{idSearch.length !== 0? <Modal movies={idSearch}/> : ""}
 <p class="title is-3">Descubre: </p> 
 <Grid movies={discover} handleIdSearch={handleIdSearch}/>
 <div class="columns">
   <div class="column is-9">
    <p class="title is-3">Tendencias: </p>
   </div>
   <div class="column is-1">
    <button class={"button is-primary" } onClick={handleChange}>Hoy</button>
   </div>
   <div class="column is-1" >
    <button class={"button is-primary"} onClick={handleChange}>Semana</button>
   </div>
 </div>
 <Grid movies={tendencia} handleIdSearch={handleIdSearch}/>
</div>} 
  </div>
)
}

export default App;
