import { useState, useEffect} from "react"
import axios from "axios"
import Grid from "./components/grid"
import Navbar from "./components/Navbar"
import SearchBar from "./components/searchBar"
import Modal from "./components/modal"
import Tendencias from "./components/Tendencias"
import Discover from "./components/Discover"

const App = () => {

const [discover, setDiscover] = useState([])
const [tendencia, setTendencia] = useState([])
const [time, setTime] = useState('day')
const [search, setSearch] = useState([])
const [idSearch, setIdSearch] = useState([])


useEffect(()=>{
axios.get('http://localhost:3000/api/discover')
.then(data => data.data)
.then(data => setDiscover(data.results.slice(0,12)))
}, [])

useEffect(()=>{
axios.get(`http://localhost:3000/api/tendencia/${time}`)
.then(data => data.data)
.then(data => setTendencia(data.results.slice(0,12)))
}, [time])

const handleSearch = (input) => {
axios.get(`http://localhost:3000/api/search/${input}`)
  .then(data => data.data)
  .then(data => setSearch(data.results))
}

const handleIdSearch = (id,media) => {
axios.get(`http://localhost:3000/api/search/${media}/${id}`)
  .then(data => setIdSearch(data.data))
}

 const handleChange = () => {
time === 'day' ? setTime("week") : setTime("day")
}

return (
  <div>
<Navbar/>
<SearchBar handleSearch={handleSearch}/>
{idSearch.length !== 0? <Modal movies={idSearch}/> : ""}

{search.length != 0 ?
<div>
 <p class="title is-3">Tu Busqueda: </p> 
 <Grid movies={search} handleIdSearch={handleIdSearch}/></div>  

:

<div>

<Discover discover={discover} handleIdSearch/>

<Tendencias tendencia={tendencia} handleIdSearch={handleIdSearch} handleChange={handleChange} time={time}/>
</div>
} 
  </div>
)
}

export default App;
