const setState= (e) => {
setInput(e.target.value)
}

const handleSubmit = (e) => {
  e.preventDefault()
  axios.get(`http://localhost:3000/api/search/${input}`)
  .then(data => data.data)
  .then(data => setSearch(data.results))
}

 return(
<div class="columns is-multiline">
