const dateNormalization = (date) => {
var meses = [
  "Enero", "Febrero", "Marzo",
  "Abril", "Mayo", "Junio", "Julio",
  "Agosto", "Septiembre", "Octubre",
  "Noviembre", "Diciembre"
]
let fecha = date.split("-")
let yyy = fecha[0]
let mes = ""
if (fecha[1][0] == 0){
mes = fecha[1][1]
}
mes = fecha[1]

let dia = fecha[2]
console.log(mes)

var fechaFormateada = dia + ' de ' + meses[mes] + ' de ' + yyy;

fecha = []

return fechaFormateada
}

export default dateNormalization
