import { useState, useEffect} from "react"
import axios from "axios"
import Grid from "./components/grid"
import Navbar from "./components/Navbar"
import SearchBar from "./components/searchBar"
import Modal from "./commons/modal"

const App = () => {

const [discover, setDiscover] = useState([])
const [tendencia, setTendencia] = useState([])
const [search, setSearch] = useState([])
const [idSearch, setIdSearch] = useState([])


useEffect(()=>{
axios.get('http://localhost:3000/api/discover')
.then(data => data.data)
.then(data => setDiscover(data.results))
}, [])

useEffect(()=>{
axios.get('http://localhost:3000/api/tendencia')
.then(data => data.data)
.then(data => setTendencia(data.results))
}, [])

const handleSearch = (input) => {
axios.get(`http://localhost:3000/api/search/${input}`)
  .then(data => data.data)
  .then(data => setSearch(data.results))
}

const handleIdSearch = (id,media) => {
axios.get(`http://localhost:3000/api/search/${media}/${id}`)
  .then(data => setIdSearch(data.data))
}

return (
  <div>
<Navbar/>
<SearchBar handleSearch={handleSearch}/>

{search.length != 0 ?

 <div><p class="title is-3">Tu Busqueda: </p> 
 <Grid movies={search} handleIdSearch={handleIdSearch}/></div>  : 

<div>
{idSearch.length !== 0? <Modal movies={idSearch}/> : ""}
<p class="title is-3">Descubre: </p> 
 <Grid movies={discover} handleIdSearch={handleIdSearch}/>
<p class="title is-3">Tendencias: </p>
 <Grid movies={tendencia} handleIdSearch={handleIdSearch}/>
</div>} 


  </div>
)
}

export default App;
