import { useState, useEffect} from "react"
import axios from "axios"
import Grid from "./components/grid"
import Navbar from "./components/Navbar"
import SearchBar from "./components/searchBar"

const App = () => {

const [discover, setDiscover] = useState([])
const [tendencia, setTendencia] = useState([])
const [search, setSearch] = useState([])

useEffect(()=>{
axios.get('http://localhost:3000/api/discover')
.then(data => data.data)
.then(data => setDiscover(data.results))
}, [])

useEffect(()=>{
axios.get('http://localhost:3000/api/tendencia')
.then(data => data.data)
.then(data => setTendencia(data.results))
}, [])

useEffect(()=>{
return 0
}, [])

return (
  <div>
<Navbar/>
<SearchBar/>

{search == [] ?

 <div><p class="title is-3">Tu Busqueda: </p> 
 <Grid movies={search}/></div>  : 

<div>
<p class="title is-3">Descubre: </p> 
 <Grid movies={discover}/>
<p class="title is-3">Tendencias: </p>
 <Grid movies={tendencia}/>
</div>} 


  </div>
)
}

export default App;
