import Card from "../commons/card"
import CardTv from "../commons/cardTV"

const Grid = ({movies, handleIdSearch}) => {
  return (
    <div class="columns is-multiline block">
     {movies.map((movie, i) => (
  <div class="column is-2" key={i}>
    {movie.media_type ==+ "tv"? 

   <CardTv data={movie} handleIdSearch={handleIdSearch}/> :

   <Card data={movie} handleIdSearch={handleIdSearch}/>}
  </div>))} 
</div>
)
}

export default Grid
